import React from 'react';
import img from '../../images/landingimg.png';
import Buttons from '../../components/Buttons';
import Cards from '../../components/Cards';

const LandingPage = () => {
  return (
    <div className='bg-[#F1F1F1] w-full min-h-screen px-10 pb-10'>
      {/* Hero Section */}
      <div className='bg-[#EBCEA8] w-full h-screen flex flex-col justify-between'>
        <div className='flex flex-wrap w-full h-3/4 gap-9 items-center justify-center'>
          {/* Text Section */}
          <div className='w-full md:w-1/2 flex flex-col gap-8 p-12 justify-center'>
            <h1 className='text-4xl md:text-5xl font-bold text-center md:text-left leading-tight'>
              Enhance Learning <br /> with Group Tutoring <br /> at EarlyLearners Hub <br /> Tutorial Services!
            </h1>
            <div className='flex justify-center md:justify-start'>
              <Buttons styleType="primary" label="ENROLL NOW" />
            </div>
          </div>
          {/* Image Section */}
          <div className='w-full md:w-1/2 flex justify-center'>
            <img src={img} alt="pic" className='h-80 md:h-[60vh] w-auto' />
          </div>
        </div>
        {/* Fun Activities Section */}
        <div className='text-center py-8'>
          <h2 className='text-2xl md:text-3xl font-semibold text-[#333]'>
            Explore Fun Activities
          </h2>
          <div className='mt-6 flex flex-wrap justify-center gap-6'>
            <Cards />
          </div>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
