import React from 'react';
import img from '../../images/landingimg.png';
import Buttons from '../../components/Buttons';
import Cards from '../../components/Cards';

const LandingPage = () => {
  return (
    <div className="bg-[#F1F1F1] w-full h-auto px-10 pb-10">
      {/* Hero Section */}
      <div className="bg-[#EBCEA8] w-full h-screen flex flex-col items-center justify-center relative">
        <div className="flex flex-col items-center md:flex-row md:items-start md:justify-between w-full h-3/4 px-4 md:px-12">
          {/* Text Section */}
          <div className="w-full md:w-1/2 text-center md:text-left md:mt-[10vh]">
            <h1 className="text-4xl md:text-5xl font-[50px] leading-snug md:leading-tight ml-[3rem] ">
              Enhance Learning <br />
              with Group Tutoring <br />
              at EarlyLearners Hub <br />
              Tutorial Services!
            </h1>
            <div className="mt-6">
              <Buttons styleType="primary" label="ENROLL NOW" />
            </div>
          </div>

          {/* Image Section */}
          <div className="hidden md:flex w-full md:w-1/2 justify-end mr-[2rem] ">
            <img
              src={img}
              alt="Fun Learning"
              className=" h-auto max-h-[75vh] "
            />
          </div>
        </div>

        
      </div>

      {/* Explore Fun Activities Section */}
      <div className="bg-white w-full py-8 text-center">
        <h2 className="text-2xl md:text-2xl font-[20px] text- black flex justify-start ">
          Explore Fun Activities
        </h2>
        <div className="mt-6 flex flex-wrap justify-center gap-6">
          <Cards />
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
